import axios from 'axios';

// Create axios instance with correct configuration
const api = axios.create({
  baseURL: 'http://localhost:9999/api', // Must match your backend URL
  timeout: 8000, // 8 second timeout
  headers: {
    'Content-Type': 'application/json',
    'Accept': 'application/json'
  },
  withCredentials: false // Set to true if using cookies/sessions
});

// Enhanced error handler
const handleApiError = (error) => {
  if (error.response) {
    // Server responded with error status
    console.error('API Error Response:', {
      status: error.response.status,
      data: error.response.data,
      headers: error.response.headers
    });
    throw new Error(error.response.data.message || `API Error: ${error.response.status}`);
  } else if (error.request) {
    // Request was made but no response received
    console.error('No response received:', error.request);
    throw new Error('Server is not responding. Please check:'
      + '\n1. Is your backend server running?'
      + '\n2. Are you using the correct port (9999)?'
      + '\n3. Is there a firewall blocking the connection?');
  } else {
    // Request setup error
    console.error('Request setup error:', error.message);
    throw new Error('Failed to create API request');
  }
};

export const getProjects = async () => {
  try {
    console.log('Attempting to fetch projects from:', api.defaults.baseURL + '/projects');
    const response = await api.get('/projects');
    console.log('Projects response:', response.data);
    return response.data;
  } catch (error) {
    handleApiError(error);
    return []; // Return empty array as fallback
  }
};
